DECLARE @ReportingRetailWeek INT = (SELECT ConfigurationValue from DataWarehouse.dbo.SysConfiguration WHERE ConfigurationKey = 'ReportingRetailWeek') DECLARE @ReportingRetailYear INT = (SELECT ConfigurationValue from DataWarehouse.dbo.SysConfiguration Where ConfigurationKey ='ReportingRetailYear') DECLARE @ReportingRetailPeriodId INT = (SELECT DISTINCT RetailPeriodId from Datawarehouse.dbo.DimDate where RetailWeek = (Select ConfigurationValue from DataWarehouse.dbo.SysConfiguration Where ConfigurationKey = 'ReportingRetailWeek' and RetailYear = @ReportingRetailYear))
DECLARE @ReportingPeriodEnd DATE = (SELECT DISTINCT RetailPeriodEnd from Datawarehouse.dbo.DimDate where RetailPeriodId = @ReportingRetailPeriodId -1)
SELECT DISTINCT 'Manually Managed Consolidations' AS Level1 , 'Reporting Consolidations' AS Level2 , 'Payroll Reporting Consolidations' AS Level3 , 'Store By AreaManager' AS Level4 , DimBusinessDivision.FinancialBusinessDivisionDescription + ' - Total' AS Level5 , CASE WHEN DimAreaManager.Code IS NULL THEN CAST(DimBusinessDivision.Code AS VARCHAR(4)) + ' - ' + 'No Area Manager Assigned' ELSE DimAreaManager.Code END AS Level6
, DimProfitCentre.Code AS Level7 , CASE WHEN MatWarehouse.AreaManagerName IS NULL THEN CAST(DimBusinessDivision.Code AS VARCHAR(4)) + ' - ' + 'No Area Manager Assigned' ELSE MatWarehouse.AreaManagerName END AS 'Name' , matwarehouse.tradingstatus , DimProfitCentre.OpenDate , DimProfitCentre.CloseDate FROM DimProfitCentre INNER JOIN DimBusinessDivision ON DimBusinessDivision.id = DimProfitCentre.CurrentBusinessDivisionId -- LEFT OUTER JOIN MatWarehouse INNER JOIN MatWarehouse ON MatWArehouse.ProfitCentreId = DimProfitCentre.Id -- LEFT OUTER JOIN dbo.DimAreaManager INNER JOIN dbo.DimAreaManager ON DimAreaManager.Id = MatWarehouse.AreaManagerId WHERE DimAreaManager.Code != 'RENA' AND DimProfitCentre.IsActive = 1 AND DimProfitCentre.OpenDate IS NOT NULL AND (DimProfitCentre.CloseDate IS NULL OR DimProfitCentre.CloseDate >= @ReportingPeriodEnd) OR DimProfitCentre.Code LIKE '85%' OR DimProfitCentre.Code = 1992 AND DimBusinessDivision.id != 11
